mapper.select:
 		select 

		from db_bdbrxx
		where org_code = #{dbBdbrxx.orgCode} and dbhtxx_id = #{dbBdbrxx.dbhtxxId}

			and bdbrmc like '%${dbBdbrxx.bdbrmc}%'


			and bdbrzjhm like '%${dbBdbrxx.bdbrzjhm}%'

		order by create_time desc limit #{pm.pageSize} offset #{pm.startCount}

select count(1) from db_bdbrxx where org_code = #{orgCode}  and dbhtxx_id = #{dbhtxxId}

	and bdbrmc like '%${bdbrmc}%'


	and bdbrzjhm like '%${bdbrzjhm}%'
