mapper.update:
    	update af_base_element_action_relation set action_id = #{actionId}, 
    	            sequence = #{sequence}, upadte_user_id = #{upadteUserId},
					update_time = now() where id = #{id}

mapper.select:
        select count(1) FROM af_base_element_action_relation where 1=1
		
			and type = #${type} 
		
		
			and baseId = #{baseId} 
		
		
			and actionId = #{actionId}

         select * from af_base_element_action_relation where 1=1
		
			and type = #${entity.type} 
		
		
			and baseId = #{entity.baseId} 
		
		
			and actionId = #{entity.actionId} 
		
         limit #{pm.pageSize} offset #{pm.startCount}
